async def our_perspective_style():
    return """
    <style>
        /* --- Base Styles & Resets --- */
        * {
            margin: 0;
            padding: 0;
            box-sizing: border-box;
        }

        /* --- Perspective Section Styles --- */
        /* The CSS is written desktop-first. JS will override styles for mobile. */
        #perspective-section {
            width: 100vw;
            display: flex;
            justify-content: center;
            align-items: center;
            position: relative;
            overflow: hidden;

            /* Padding using viewport units */
            padding: 10vh 5vw;
        }

        .perspective-container {
            width: 80vw;
            max-width: 93.75vw; /* Cap width on very large screens */
            text-align: center;
            position: relative;
            z-index: 1;
        }

        .perspective-container .section-title {
            font-weight: 700;
            
            /* Sizing and spacing using viewport units */
            font-size: 3vw;
            margin-bottom: 8vh;
        }
        
        /* Style for the highlighted word "Perspective" */
        .perspective-container .section-title .highlight {
            color: #38bdf8; /* A bright, modern blue */
        }

        .cards-grid {
            display: grid;
            grid-template-columns: repeat(3, 1fr); /* Creates the 3-column layout */
            
            /* Gap using viewport units */
            gap: 4vw;
            transition: all 0.3s ease-in-out;
            margin-top: 7vw;
        }

        .perspective-card {
            display: flex;
            flex-direction: column;
            align-items: flex-start;
            text-align: left;
        }

        .icon-container {
            background-color: #ffffff;
            display: inline-flex;
            justify-content: center;
            align-items: center;
            
            /* Sizing and spacing using viewport units */
            width: 3.5vw;
            height: 3.5vw;
            border-radius: 0.8vw;
            margin-bottom: 3vh;
        }

        .icon-container img {
            /* Icon size relative to its container */
            width: 40%;
            height: 40%;
        }

        .perspective-card h3 {
            font-weight: 600;
            line-height: 1.2;

            /* Sizing and spacing using viewport units */
            font-size: 1.6vw;
            margin-bottom: 1.5vh;
        }

        .perspective-card p {
            color: #bdbdbd;
            line-height: 1.6;
            
            /* Sizing and spacing using viewport units */
            font-size: 1.1vw;
            max-width: 30ch; /* Limits line length for readability */
        }
        .eclipse-glow {
            position: absolute;
            width: 80vw;
            height: 40vw;
            top: 60%;
            left: 95%;
            transform: translate(-50%, -50%);
            background-image: url('../../Resources/Images/AboutUs/ecliplse_glow.png');
            background-size: contain;
            background-repeat: no-repeat;
            background-position: center;
            z-index: -1;
            opacity: 0.5;
            pointer-events: none;
    }

    </style>
    
"""


