async def contact_banner_style():
    return """
<style>
        .contact-hero-section {
            position: relative;
            width: 100%; /* Use 100% to avoid overflow issues, vw can cause them */
            height: 40vh; /* Default height for desktop */
            display: flex;
            justify-content: center;
            align-items: center;
            /* Using a high-quality placeholder image that matches the theme */
            background-image: url('../../Resources/Images/ContactUs/hero_background.png');
            background-size: cover;
            background-position: center;
            overflow: hidden;
            transition: height 0.3s ease-in-out;
            top: 2vh;
            margin-top: 5vw;
        }

        .contact-hero-title {
            position: relative;
            z-index: 2;
            font-family: 'Poppins', sans-serif;
            font-weight: 700;
            font-size: 8vw; /* Default font size for desktop */
            text-transform: uppercase;
            letter-spacing: 0.2vw;
            text-align: center;
            
            /* Metallic gradient effect to match the image */
            background: linear-gradient(180deg, #FFFFFF 40%, #B0B0B0 90%);
            -webkit-background-clip: text;
            -webkit-text-fill-color: transparent;
            background-clip: text;
            text-fill-color: transparent;
            
            /* Subtle shadow for depth */
            text-shadow: 0 0.2vw 0.5vw rgba(0, 0, 0, 0.5);
            transition: font-size 0.3s ease-in-out;
        }
    </style>
"""
